import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import "./EquipmentList.css";

function EquipmentList() {
  const [equipment, setEquipment] = useState([]);
  const [loading, setLoading] = useState(true);
  
  const navigate = useNavigate();
  const userRole = localStorage.getItem("userRole");

  useEffect(() => {
    fetchEquipment();
  }, []);

  const fetchEquipment = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/equipment/all");
      setEquipment(res.data);
      setLoading(false);
    } catch (err) {
      console.error("Error fetching equipment");
      setLoading(false);
    }
  };

  return (
    <div className="equipment-page">
      <div className="list-header">
        <div>
          <h2>Asset Inventory</h2>
          <p>Total Managed Assets: {equipment.length}</p>
        </div>
        
        {/* Only Managers can add new equipment */}
        {userRole === "Manager" && (
          <button className="add-btn" onClick={() => navigate("/add-equipment")}>
            + Register New Asset
          </button>
        )}
      </div>

      {loading ? (
        <div className="loader">Loading Assets...</div>
      ) : (
        <div className="table-container">
          <table className="custom-table">
            <thead>
              <tr>
                <th>Machine Name</th>
                <th>Serial Number</th>
                <th>Department</th>
                <th>Team</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {equipment.map((item) => (
                <tr key={item._id}>
                  <td><strong>{item.name}</strong><br/><small>{item.location}</small></td>
                  <td>{item.serialNumber}</td>
                  <td>{item.department}</td>
                  <td><span className={`team-tag ${item.maintenanceTeam.toLowerCase()}`}>{item.maintenanceTeam}</span></td>
                  <td>
                    <span className={`status-pill ${item.status.toLowerCase().replace(" ", "-")}`}>
                      {item.status}
                    </span>
                  </td>
                  <td>
                    {/* Requesters click this to report a breakdown */}
                    {userRole === "Requester" && item.status !== "Scrapped" && (
                      <button 
                        className="report-btn"
                        onClick={() => navigate(`/create-request?equipId=${item._id}`)}
                      >
                         Report Breakdown
                      </button>
                    )}
                    {userRole === "Manager" && (
                      <button className="edit-btn">Edit</button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default EquipmentList;
